const audioData = $input.item.json.body.audio.data;
// Only process if data exists
if (!audioData) return { json: {} };

return {
  binary: {
    data: {
      data: audioData,
      mimeType: $input.item.json.body.audio.mimeType || 'audio/ogg',
      fileName: 'voice_message.ogg'
    }
  },
  json: $input.item.json
};

try {
    // 1. Get raw content from OpenAI
    // In JSON Mode, it's usually inside message.content
    let raw = $input.item.json.message?.content || $input.item.json.text;

    // 2. Parse it
    if (typeof raw === 'string') {
        raw = JSON.parse(raw);
    }

    // 3. Validation: Ensure 'type' exists
    if (!raw.type) {
        // Fallback if AI hallucinated a bad format
        return {
            json: {
                type: 'message',
                text: typeof raw === 'string' ? raw : JSON.stringify(raw)
            }
        };
    }

    return { json: raw };

} catch (error) {
    // 4. Catastrophic Failure Handler
    // If OpenAI returns invalid JSON or something breaks, return a safe error message
    return {
        json: {
            type: 'message',
            text: "خطا در پردازش هوش مصنوعی. لطفاً دوباره تلاش کنید."
        }
    };
}
